import { View, Text, SafeAreaView, Image } from "react-native";
import React from "react";
import { DetailsHeader } from "./Details";
import Lottie from "lottie-react-native";
import { CardTitle } from "../components/SubInfo";
import { FONTS, COLORS } from "../constants";

import { SIZES } from "../constants";

const DetailsDesc = ({ data }) => {
  return (
    <>
      <View
        style={{
          width: "100%",
          flexDirection: "row",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <CardTitle
          title={data.name}
          subTitle={data.creator}
          titleSize={SIZES.extraLarge}
          subTitleSize={SIZES.font}
        />
      </View>

      <View style={{ marginVertical: SIZES.extraLarge * 0.3 }}>
        <Text
          style={{
            fontSize: SIZES.font,
            fontFamily: FONTS.semiBold,
            color: COLORS.primary,
          }}
        >
          Description
        </Text>
        <View
          style={{
            marginTop: SIZES.base,
          }}
        >
          <Text
            style={{
              color: "black",
              fontSize: SIZES.small,
              fontFamily: FONTS.regular,
              lineHeight: SIZES.large,
              textAlign: "justify",
            }}
          >
            {data.description}
          </Text>
        </View>
      </View>
    </>
  );
};

const Solution = ({ route, navigation }) => {
  const { levelName, levelSub,solutionImgSrc } = route.params;
  const data = {
    description: "Hello",
    name: levelName,
    creator: levelSub,
  };
  return (
    <SafeAreaView>
      <Lottie source={solutionImgSrc} autoPlay/>
      <View style={{ width: "100%", height: 373 }}>
        <DetailsHeader data={data} navigation={navigation} />
        <View style={{ padding: SIZES.font }}>
          <DetailsDesc data={data} />
        </View>
      </View>
    </SafeAreaView>
  );
};

export default Solution;
